package leetcode;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Created by haodongl on 12/4/15.
 */
public class ConcatenationSubstr {
    public List<Integer> findSubstring(String s, String[] words) {

        List<Integer> result = new ArrayList<Integer>();
        if(s.length()==0 || s == null || words.length ==0) return result;
        int wordsLength = words[0].length();
        int wordsNum = words.length;

        Map<String, Integer> wordsCountMap = new HashMap<String, Integer>();

        for(String word : words){
            if(!wordsCountMap.containsKey(word)){
                wordsCountMap.put(word, 1);
            }else {
                wordsCountMap.put(word, wordsCountMap.get(word) + 1);
            }
        }

        for(int k =0; k< wordsLength; k++){
            for (int i = 0 + k; i < s.length() - wordsLength * wordsNum + 1; i+=wordsLength) {
                String candidateStr = s.substring(i, i+wordsLength*wordsNum);
                Map<String, Integer> foundCounterMap = new HashMap<String, Integer>();
                boolean isValidStart = true;
                for(int j=0; j + wordsLength <= candidateStr.length(); j+=wordsLength){
                    String tmp = candidateStr.substring(j, j+wordsLength);
                    if(!wordsCountMap.containsKey(tmp)){
                        isValidStart = false;
                        i = i+ j;
                        break;
                    }else {
                        if(!foundCounterMap.containsKey(tmp)) foundCounterMap.put(tmp, 0);
                        if(foundCounterMap.get(tmp) >= wordsCountMap.get(tmp)){
                            isValidStart = false;
                            for(int l=0; l < candidateStr.length(); l+=wordsLength){
                                if(candidateStr.substring(l, l+wordsLength).equals(tmp)){
                                    i += l;
                                    break;
                                }
                            }
                            break;
                        }else {
                            foundCounterMap.put(tmp,  foundCounterMap.get(tmp) + 1);
                        }
                    }
                }
                if(isValidStart) result.add(i);
            }
        }

        return result;
    }


    public static void main(String[] args) {
        long startTime = System.currentTimeMillis();
        String[] words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
        String s = "";
//        String[] words = {"foo", "bar"};
//        String s = "barfoothefoobarman";
        System.out.println(new ConcatenationSubstr().findSubstring(s, words));
        long elapse = System.currentTimeMillis() - startTime;
        System.out.println(elapse);
    }
}
